(defn reduce-fast [f val coll]
  "Wraps a standard reduce with automatical transient-persistent transiton"
  (-> (reduce f (transient val) coll)
      (persistent!)))

(defn group-by-and-compacted [group-fn compact-fn coll]
  "Returns a map with keys extracted from coll items
   by group-fn"
  (reduce-fast (fn [acc v]
                 (let [k (group-fn v)]
                   (assoc! acc k (conj (get acc k []) (compact-fn v)))))
               {}
               coll))
