(defn group-by-and-compacted [group-fn compact-fn coll]
  "Returns a map with keys extracted from coll items
   by group-fn"
  (->> coll
       (reduce (fn [acc v]
                 (let [k (group-fn v)]
                   (assoc! acc k (conj (get acc k []) (compact-fn v)))))
               (transient {}))
       (persistent!)))

(defn map-by-key [key coll]
  "Returns a hash-map from coll.
   The value of key (VK) is extracted from every item of collection.
   Result keys are VK and result values are original item without key "
  (->> coll
       (reduce (fn [acc v]
                 (assoc! acc (get v key) (dissoc v key)))
               (transient {}))
       (persistent!)))
